Scripts.OnLeave:
GameTooltip:Hide();
self.showingTooltip = false;
local isSelected = PetJournal.pcIndex == self.index;
PetJournal_UpdatePetCardToggleButtons();

Scripts.OnClick:
if ( IsModifiedClick() ) then
	--SpellButton_OnModifiedClick(self, button);
else
	PetJournal_ShowPetSelect(self);
end

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip);
end

Scripts.OnClick:
local type, petID = GetCursorInfo();
if type == "battlepet" then
	C_PetJournal.SetPetLoadOutInfo(self:GetParent():GetID(), petID);
	PetJournal_UpdatePetLoadOut();
	ClearCursor();
end

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip);
end

Scripts.OnClick:
ToggleAchievementFrame();
local i = 1;
local button = _G["AchievementFrameCategoriesContainerButton"..i];
while button do
	if button.element.id == PET_ACHIEVEMENT_CATEGORY then
		button:Click();
		return;
	end

	i = i + 1;
	button = _G["AchievementFrameCategoriesContainerButton"..i];
end
